mapper.select:
select adjustment.ID, adjustment.PRICE_LIST_NAME, adjustment.TYPE, adjustment.EXPIRY_DATE, adjustment.REGION, adjustment.STOREFRONT_TYPE, adjustment.EXCHANGE_RATE, adjustment.CURRENCY, adjustment.PRICE_LIST_ID, adjustment.PROCESS_INSTANCE_ID, adjustment.STATUS, adjustment.CREATED_BY, adjustment.CREATION_DATE, adjustment.LAST_UPDATE_DATE, adjustment.DOCNUM, adjustment.ERP_STATUS, adjustment.ERP_ERROR_LOG, adjustment.PLATFORM_TYPE from ESHOP_PRODUCT_ADJUSTMENT_RECORD adjustment
adjustment.PRICE_LIST_ID = #{priceListId}
and adjustment.id = #{id}
and adjustment.docnum like concat(concat('%',#{docnum}),'%')
and adjustment.erp_status = #{erpStatus}
order by adjustment.CREATION_DATE desc,adjustment.id
